<?php//// Базовый контроллер//abstract class C_Base extends C_Controller{	protected $title;		// заголовок страницы	protected $content;		// содержание страницы	protected $needLogin;	// необходима ли авторизация	protected $user;		// авторизованный пользователь || null	//	// Конструктор.	//	function __construct()	{			$this->needLogin = false;		$this->user = file_get_contents('http://users-api/index.php?id');	}		protected function before()	{		if($this->needLogin && $this->user === null)			$this->redirect('/auth/login');			$this->title = 'WEB-версия';		$this->content = '';	}		//	// Генерация базового шаблона	//		public function render()	{		$vars = array('title' => $this->title, 'content' => $this->content);			$page = $this->Template('v/v_main.php', $vars);						echo $page;	}	/*	public function getApiMethod($request){		switch ($request) {			case 'GET':				$this->getUser($_GET['id']);			break;			case 'POST':				if (isset($_POST['logout'])) {					$this->logoutUser();				}				else {					isset($_POST['UserEmail']) ? $this->addUser($_POST, $_FILES) : $this->loginUser($_POST);				}			break;			case 'PUT':				$this->editUser($_POST);			break;				}	}	*/}